package ch03;

public class CompareTest01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=10;
		int y=2;
		System.out.println(x==y);
		System.out.println(x!=y);
		System.out.println(x>y);
		System.out.println(x<y);
		System.out.println(x>=y);
		System.out.println(x<=y);
		
		// 삼항 연산자
		int a=10;
		System.out.println(x%2==0 ? "짝수" : "홀수");
		// 참이면 전자 출력 , 거짓이면 후자 출력


	}

}
