package ch03;

public class IfTest01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String Myjob1="홍길동";
		String Myjob2="이순신";
		String Myjob3="이성계";
		String Myjob4="정도전";
		
		if (Myjob1=="홍길동") {
			System.out.println("홍길동은 의적");
		}
		else if (Myjob2=="이순신") {
			System.out.println("이순신은 장군");
		}
		else if (Myjob3=="이성계") {
			System.out.println(" 이성계는 왕");
		}
		else {
			System.out.println("정도전은 책사");
		}
	}

}
